    <script>
        function berhasilTerkirim() {
            Swal.fire({
                icon: 'success',
                title: 'Berhasil terkirim',
                text: 'Ucapan dan doa berhasil terkirim',
            })
        }

        function tidakLengkap() {
            Swal.fire({
                icon: 'error',
                title: 'Ucapan dan Doa tidak terkirim',
                text: 'Mohon lengkapi semua kolom 🙏',
            })
        }

        function loadPage(page) {
            $.ajax({
                url: 'select-data.php',
                type: 'POST',
                data: {
                    page: page
                },
                beforeSend: function() {
                    // Menambahkan spinner Bootstrap saat loading
                    $('#content').html('<div class="d-flex justify-content-center mt-4"><div class="spinner-border text-primary" role="status"><span class="visually-hidden"></span></div></div>');
                },
                success: function(data) {
                    $('#content').html(data);
                },
            });
        }

        function loadData() {
            $.ajax({
                url: 'select-data.php',
                type: 'POST',
                success: function(data) {
                    $("#content").html(data);
                }
            });
        }

        $(document).ready(function() {
            // Load halaman pertama saat halaman dimuat
            loadPage(1);

            $(".halaman").on('click', function() {
                var page = $(this).attr('id');
                loadPage(page);
            });

            loadData();

            $("#btn").on("click", function(e) {
                e.preventDefault();
                var name = $("#name").val();
                var msg = $("#msg").val();
                var asal = $("#asal").val();
                var present = $("#present").val();

                // Validasi form sebelum mengirim data ke server
                if (name == "" || msg == "" || asal == "" || present == "") {
                    tidakLengkap();
                    return false;
                }

                $.ajax({
                    url: 'insert-data.php',
                    type: 'POST',
                    data: {
                        name: name,
                        msg: msg,
                        asal: asal,
                        present: present
                    },
                    success: function(data) {
                        if (data == 1) {
                            loadData();
                            berhasilTerkirim();
                            $("#form").trigger("reset");
                        } else {
                            alert("Gagal Terkirim");
                        }
                    }
                });
            });
        });
    </script>

